import {Injectable} from "@angular/core"

@Injectable()
export class ValidatorService {
    
    validate(validationTypes:string[], value:string):Object {
        var regex:RegExp, result = {finalResult : true };
        
        if(validationTypes.indexOf('url') != -1){
            regex = /^([a-zA-Z0-9]([a-zA-Z0-9\-]{0,61}[a-zA-Z0-9])?\.)+[a-zA-Z]{2,6}$/
            this.populateResult('url',value, regex, result);             
        }
        if(validationTypes.indexOf('number') != -1){
            regex = /^[-+]?\d*\.?\d*$/
            this.populateResult('number',value, regex, result);
        }         
        if(validationTypes.indexOf('email') != -1){
            regex = /^[-a-z0-9~!$%^&*_=+}{\'?]+(\.[-a-z0-9~!$%^&*_=+}{\'?]+)*@([a-z0-9_][-a-z0-9_]*(\.[-a-z0-9_]+)*\.(aero|arpa|biz|com|coop|edu|gov|info|int|mil|museum|name|net|org|pro|travel|mobi|[a-z][a-z])|([0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}))(:[0-9]{1,5})?$/i
            this.populateResult('email',value, regex, result);
        }
        if(validationTypes.indexOf('usphone') != -1){ 
            regex = /((\(\d{3}\)?)|(\d{3}))([\s-./]?)(\d{3})([\s-./]?)(\d{4})/
            this.populateResult('usphone',value, regex, result);
        }
        if(validationTypes.indexOf('uszip') != -1){ 
            regex = /^((\d{5}-\d{4})|(\d{5})|([AaBbCcEeGgHhJjKkLlMmNnPpRrSsTtVvXxYy]\d[A-Za-z]\s?\d[A-Za-z]\d))$/
            this.populateResult('uszip',value, regex, result);
        }
        if(validationTypes.indexOf('credit-card') != -1){
            regex = /^(?:(?4[0-9]{12}(?:[0-9]{3})?)|(?5[1-5][0-9]{14})|(?6(?:011|5[0-9]{2})[0-9]{12})|(?3[47][0-9]{13})|(?3(?:0[0-5]|[68][0-9])?[0-9]{11})|(?(?:2131|1800|35[0-9]{3})[0-9]{11}))$/;
            this.populateResult('credit-card',value, regex, result);
        }
        console.log(result);
        return result;      
    }
    
    populateResult(type:string, value:string, regex:RegExp, result){
        result[type] = regex.test(value);
        result.finalResult = result.finalResult && result[type];
    }    
}
